pragma solidity ^0.5.8;

import 'openzeppelin-solidity/contracts/ownership/Ownable.sol';

// @title Manage all the ballots by the owner
contract BallotManager is Ownable {

    // The individual sentence of the whole transcription
    struct Sentence {
        string hash;
        uint voteCount;
    }

    // One event has one ballot
    struct Ballot {
        uint numPos; // num of positions(initial sentences) in the transcription
        address organizer; // the organizer of this ballot
        mapping (address => bool) isAttendee; // validated attendees are recorded here
        mapping (uint => Sentence[]) speech; // sentences at each position to be voted
        mapping (address => mapping (uint => bool )) voted; // record whether an attendee has voted for each pos
        mapping (address => mapping (uint => bool)) uploaded; // record whether an attendee has uploaded a sentence for each pos
    }

    // This declares who is the organizer that
    // stores a `bool`
    mapping (address => bool) public isOrganizer;
    mapping (string => Ballot) public eventToBallot;

    function addOrganizer(address _organizer) external onlyOwner {
        require(!isOrganizer[_organizer], "The organizer has already been added.");
        require(owner() != _organizer, "Owner can not add themselves as the organizer.");
        isOrganizer[_organizer] = true;
    }

    function approveEvent(address _organizer, string memory _eventId) public onlyOwner {
        require(isOrganizer[_organizer], "Only organizer can hold events.");
        require(eventToBallot[_eventId].organizer == address(0), "Only unassigned eventId can be approved.");
        eventToBallot[_eventId].organizer = _organizer;
    }
}
